import Navbar from "../../Componentes/Navbar";
import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import "./NovoProjeto.css";


function NovoProjeto() {
  const [projectData, setProjectData] = useState({
    name: '',
    creationDate: { month: '01', year: '2024' },
    logo: null,
    students: '',
    advisor: '',
    problem: '',
    solution: '',
    images: null,
    documentation: '',
    isFeatured: false,
    featuredPosition: '',
    author: '',
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;

    if (name === 'month' || name === 'year') {
      setProjectData({
        ...projectData,
        creationDate: { ...projectData.creationDate, [name]: value }
      });
    } else if (type === 'checkbox') {
      setProjectData({ ...projectData, [name]: checked });
    } else if (type === 'file') {
      setProjectData({ ...projectData, [name]: files });
    } else {
      setProjectData({ ...projectData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.keys(projectData).forEach((key) => {
      if (key === 'images') {
        Array.from(projectData.images || []).forEach((file) => {
          formData.append('images', file);
        });
      } else if (key === 'logo' && projectData.logo) {
        formData.append('logo', projectData.logo[0]);
      } else if (key === 'creationDate') {
        formData.append('creationDate', `${projectData.creationDate.month}-${projectData.creationDate.year}`);
      } else if (key !== 'featuredPosition' || projectData.isFeatured) {
        formData.append(key, projectData[key]);
      }
    });

    try {
      const response = await axios.post('http://localhost:5000/projects', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      setMessage(response.data.message);
      document.getElementById("closeModalButton").click(); // Fecha o modal

    } catch (error) {
      setMessage(`Erro ao adicionar o projeto: ${error.response?.data?.message || error.message}`);
    }
  };

  return (
    <>
      <Navbar />
      <div className="container mt-4">
        <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#novoProjetoModal">
          Adicionar Novo Projeto
        </button>

        {/* MODAL */}
        <div className="modal fade" id="novoProjetoModal" tabIndex="-1" aria-labelledby="novoProjetoModalLabel" aria-hidden="true">
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="novoProjetoModalLabel">Adicionar Novo Projeto</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
              </div>

              <div className="modal-body">
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label htmlFor="name" className="form-label">Nome do Projeto</label>
                    <input type="text" className="form-control" id="name" name="name" value={projectData.name} onChange={handleChange} required />
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Data de Criação</label>
                    <div className="d-flex gap-2">
                      <select name="month" value={projectData.creationDate.month} onChange={handleChange} className="form-select">
                        {[..."01 02 03 04 05 06 07 08 09 10 11 12".split(" ")].map((m, i) => (
                          <option key={i} value={m}>{new Date(2000, i).toLocaleString('pt-BR', { month: 'long' })}</option>
                        ))}
                      </select>
                      <select name="year" value={projectData.creationDate.year} onChange={handleChange} className="form-select">
                        {Array.from({ length: new Date().getFullYear() - 1999 }, (_, i) => 2000 + i).map(year => (
                          <option key={year} value={year}>{year}</option>
                        ))}
                      </select>
                    </div>
                  </div>

                  <div className="mb-3">
                    <label htmlFor="logo" className="form-label">Logo</label>
                    <input type="file" className="form-control" id="logo" name="logo" onChange={handleChange} required />
                  </div>

                  <div className="mb-3">
                    <label htmlFor="students" className="form-label">Alunos</label>
                    <input type="text" className="form-control" id="students" name="students" value={projectData.students} onChange={handleChange} required />
                  </div>

                  <div className="mb-3">
                    <label htmlFor="problem" className="form-label">Problemática</label>
                    <textarea className="form-control" id="problem" name="problem" value={projectData.problem} onChange={handleChange} required></textarea>
                  </div>

                  <div className="mb-3">
                    <label htmlFor="solution" className="form-label">Solução</label>
                    <textarea className="form-control" id="solution" name="solution" value={projectData.solution} onChange={handleChange} required></textarea>
                  </div>

                  <div className="mb-3">
                    <div className="form-check">
                      <input className="form-check-input" type="checkbox" id="isFeatured" name="isFeatured" checked={projectData.isFeatured} onChange={handleChange} />
                      <label className="form-check-label" htmlFor="isFeatured">Destaque na Home</label>
                    </div>
                  </div>

                  <div className="modal-footer">
                    <button type="button" className="btn btn-secondary" data-bs-dismiss="modal" id="closeModalButton">Fechar</button>
                    <button type="submit" className="btn btn-primary">Adicionar Projeto</button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>

        {message && <div className="alert alert-info mt-4">{message}</div>}
      </div>
    </>
  );
}

export default NovoProjeto;
