import Navbar from "../../Componentes/Navbar";
import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

function NovoProjeto() {
  const [projectData, setProjectData] = useState({
    name: '',
    creationDate: { semester: '1', year: '2024' }, // Padrão inicial
    module: '1',
    logo: null,
    students: '',
    advisor: '',
    problem: '',
    solution: '',
    images: null,
    documentation: '',
    isFeatured: false,
    featuredPosition: '',
    author: '',
  });

  const [message, setMessage] = useState('');

  // Atualiza os campos do formulário
  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;

    if (name === 'semester' || name === 'year') {
      setProjectData({
        ...projectData,
        creationDate: { ...projectData.creationDate, [name]: value }
      });
    } else if (type === 'checkbox') {
      setProjectData({ ...projectData, [name]: checked });
    } else if (type === 'file') {
      setProjectData({ ...projectData, [name]: files });
    } else {
      setProjectData({ ...projectData, [name]: value });
    }
  };

  // Envio do formulário
  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.keys(projectData).forEach((key) => {
      if (key === 'images' || key === 'logo') {
        Array.from(projectData[key] || []).forEach((file) => {
          formData.append(key, file);
        });
      } else if (key === 'creationDate') {
        formData.append(key, `${projectData.creationDate.semester}-${projectData.creationDate.year}`);
      } else {
        formData.append(key, projectData[key]);
      }
    });

    try {
      const response = await axios.post('http://localhost:5000/projects', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setMessage(response.data.message);
    } catch (error) {
      setMessage('Erro ao adicionar o projeto: ' + error.message);
    }
  };

  return (
    <>
      <Navbar />
      <div className="container">
        <h1 className="mb-4">Adicionar Novo Projeto</h1>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="name" className="form-label">Nome do Projeto</label>
            <input
              type="text"
              className="form-control"
              id="name"
              name="name"
              value={projectData.name}
              onChange={handleChange}
              required
            />
          </div>

          {/* Data de Criação */}
          <div className="mb-3">
            <label className="form-label">Data de Criação</label>
            <div className="d-flex gap-2">

              <select name="semester" value={projectData.creationDate.semester} onChange={handleChange} className="form-select">
                <option value="1">Módulo 1</option>
                <option value="2">Módulo 2</option>
              </select>
              
              <select name="year" value={projectData.creationDate.year} onChange={handleChange} className="form-select">
                {Array.from({ length: new Date().getFullYear() - 1999 }, (_, i) => 2000 + i).map(year => (
                  <option key={year} value={year}>{year}</option>
                ))}
              </select>
            </div>
          </div>

          {/* Módulo */}
          <div className="mb-3">
            <label htmlFor="module" className="form-label">Módulo</label>
            <select name="module" id="module" value={projectData.module} onChange={handleChange} className="form-select">
              <option value="1">Módulo 1</option>
              <option value="2">Módulo 2</option>
            </select>
          </div>

          <div className="mb-3">
            <label htmlFor="logo" className="form-label">Logo</label>
            <input type="file" className="form-control" id="logo" name="logo" onChange={handleChange} required />
          </div>

          <div className="mb-3">
            <label htmlFor="students" className="form-label">Alunos</label>
            <input type="text" className="form-control" id="students" name="students" value={projectData.students} onChange={handleChange} required />
          </div>

          <div className="mb-3">
            <label htmlFor="advisor" className="form-label">Orientador</label>
            <input type="text" className="form-control" id="advisor" name="advisor" value={projectData.advisor} onChange={handleChange} required />
          </div>

          <div className="mb-3">
            <label htmlFor="problem" className="form-label">Problemática</label>
            <textarea className="form-control" id="problem" name="problem" value={projectData.problem} onChange={handleChange} required></textarea>
          </div>

          <div className="mb-3">
            <label htmlFor="solution" className="form-label">Solução</label>
            <textarea className="form-control" id="solution" name="solution" value={projectData.solution} onChange={handleChange} required></textarea>
          </div>

          <div className="mb-3">
            <label htmlFor="images" className="form-label">Imagens</label>
            <input type="file" className="form-control" id="images" name="images" onChange={handleChange} multiple required />
          </div>

          <div className="mb-3">
            <label htmlFor="documentation" className="form-label">Documentação</label>
            <input type="text" className="form-control" id="documentation" name="documentation" placeholder="Link para o GitHub ou PDF" value={projectData.documentation} onChange={handleChange} required />
          </div>

          <div className="mb-3">
            <div className="form-check">
              <input className="form-check-input" type="checkbox" id="isFeatured" name="isFeatured" checked={projectData.isFeatured} onChange={handleChange} />
              <label className="form-check-label" htmlFor="isFeatured">Destaque na Home</label>
            </div>
          </div>

          {projectData.isFeatured && (
            <div className="mb-3">
              <label htmlFor="featuredPosition" className="form-label">Posição de Destaque (1 a 3)</label>
              <input type="number" className="form-control" id="featuredPosition" name="featuredPosition" value={projectData.featuredPosition} onChange={handleChange} min="1" max="3" required />
            </div>
          )}

          <div className="mb-3">
            <label htmlFor="author" className="form-label">Autor da Postagem</label>
            <input type="text" className="form-control" id="author" name="author" value={projectData.author} onChange={handleChange} required />
          </div>

          <button type="submit" className="btn btn-primary">Adicionar Projeto</button>
        </form>

        {message && <div className="alert alert-info mt-4">{message}</div>}
      </div>
    </>
  );
}

export default NovoProjeto;
