// Importações essenciais
import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const Admin = () => {
  // Estados para armazenar os dados do formulário
  const [projectData, setProjectData] = useState({
    name: '',
    creationDate: '',
    logo: null,
    students: '',
    advisor: '',
    problem: '',
    solution: '',
    images: null,
    documentation: '',
    isFeatured: false,
    featuredPosition: '',
    author: '',
  });

  const [message, setMessage] = useState(''); // Para feedback ao usuário

  // Função para lidar com mudanças nos campos do formulário
  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === 'checkbox') {
      setProjectData({ ...projectData, [name]: checked });
    } else if (type === 'file') {
      setProjectData({ ...projectData, [name]: files });
    } else {
      setProjectData({ ...projectData, [name]: value });
    }
  };

  // Função para envio do formulário
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Criar um FormData para enviar os arquivos e os dados do formulário
    const formData = new FormData();
    Object.keys(projectData).forEach((key) => {
      if (key === 'images' || key === 'logo') {
        Array.from(projectData[key]).forEach((file) => {
          formData.append(key, file);
        });
      } else {
        formData.append(key, projectData[key]);
      }
    });

    try {
      const response = await axios.post('http://localhost:5000/projects', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setMessage(response.data.message);
    } catch (error) {
      setMessage('Erro ao adicionar o projeto: ' + error.message);
    }
  };

  return (
    <div className="container ">
      <h1 className="mb-4">Adicionar Novo Projeto</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="name" className="form-label">Nome do Projeto</label>
          <input
            type="text"
            className="form-control"
            id="name"
            name="name"
            value={projectData.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="creationDate" className="form-label">Data de Criação</label>
          <input
            type="text"
            className="form-control"
            id="creationDate"
            name="creationDate"
            placeholder="Semestre e Ano (ex: 1-2025)"
            value={projectData.creationDate}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="logo" className="form-label">Logo</label>
          <input
            type="file"
            className="form-control"
            id="logo"
            name="logo"
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="students" className="form-label">Alunos</label>
          <input
            type="text"
            className="form-control"
            id="students"
            name="students"
            value={projectData.students}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="advisor" className="form-label">Orientador</label>
          <input
            type="text"
            className="form-control"
            id="advisor"
            name="advisor"
            value={projectData.advisor}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="problem" className="form-label">Problemática</label>
          <textarea
            className="form-control"
            id="problem"
            name="problem"
            value={projectData.problem}
            onChange={handleChange}
            required
          ></textarea>
        </div>
        <div className="mb-3">
          <label htmlFor="solution" className="form-label">Solução</label>
          <textarea
            className="form-control"
            id="solution"
            name="solution"
            value={projectData.solution}
            onChange={handleChange}
            required
          ></textarea>
        </div>
        <div className="mb-3">
          <label htmlFor="images" className="form-label">Imagens</label>
          <input
            type="file"
            className="form-control"
            id="images"
            name="images"
            onChange={handleChange}
            multiple
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="documentation" className="form-label">Documentação</label>
          <input
            type="text"
            className="form-control"
            id="documentation"
            name="documentation"
            placeholder="Link para o GitHub ou PDF"
            value={projectData.documentation}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <div className="form-check">
            <input
              className="form-check-input"
              type="checkbox"
              id="isFeatured"
              name="isFeatured"
              checked={projectData.isFeatured}
              onChange={handleChange}
            />
            <label className="form-check-label" htmlFor="isFeatured">
              Destaque na Home
            </label>
          </div>
        </div>
        {projectData.isFeatured && (
          <div className="mb-3">
            <label htmlFor="featuredPosition" className="form-label">Posição de Destaque (1 a 3)</label>
            <input
              type="number"
              className="form-control"
              id="featuredPosition"
              name="featuredPosition"
              value={projectData.featuredPosition}
              onChange={handleChange}
              min="1"
              max="3"
              required
            />
          </div>
        )}
        <div className="mb-3">
          <label htmlFor="author" className="form-label">Autor da Postagem</label>
          <input
            type="text"
            className="form-control"
            id="author"
            name="author"
            value={projectData.author}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Adicionar Projeto</button>
      </form>
      {message && <div className="alert alert-info mt-4">{message}</div>}
    </div>
  );
};

export default Admin;
